window.jQuery = window.$ = jQuery;


/*-----------------------------------------------------------------------------------*/
/*	MENU
/*-----------------------------------------------------------------------------------*/
function calculateScroll() {
	var contentTop      =   [];
	var contentBottom   =   [];
	var winTop      =   $(window).scrollTop();
	var rangeTop    =   200;
	var rangeBottom =   500;
	$('.main_menu').find('.scroll_btn a').each(function(){
		contentTop.push( $( $(this).attr('href') ).offset().top );
		contentBottom.push( $( $(this).attr('href') ).offset().top + $( $(this).attr('href') ).height() );
	});
	$.each( contentTop, function(i){
		if ( winTop > contentTop[i] - rangeTop && winTop < contentBottom[i] - rangeBottom ){
			$('.main_menu li')
			.removeClass('active')
			.eq(i).addClass('active');			
		}
	});
};

jQuery(document).ready(function() {
	//MobileMenu
	if ($(window).width() < 768){
		jQuery('.main_menu').prepend('<a href="javascript:void(0)" class="menu_toggler"><i class="fa fa-bars"></i></a>');
		jQuery('header .main_menu ul').hide();
		jQuery('.menu_toggler, .main_menu ul li a').click(function(){
			jQuery('header .main_menu ul').slideToggle(300);
		});
	}
		
	
	$(window).scroll(function(event) {
		calculateScroll();
	});
	$('.main_menu ul li.scroll_btn a, .mobile_menu ul li a').click(function() {  
		$('html, body').animate({scrollTop: $(this.hash).offset().top - 64}, 1000);
		return false;
	});
	
	
	//Fixed Menu
	var fixed_menu = true;

	if (jQuery('header').size() && fixed_menu == true) {
		setInterval(scrolled_menu, 0);
	}
	
});

function scrolled_menu() {
	if (jQuery(window).scrollTop()) {
		jQuery('header').addClass('menu_fixed');
	} else {
		jQuery('header').removeClass('menu_fixed');
	}
};


	

/*-----------------------------------------------------------------------------------*/
/*	FLEXSLIDER
/*-----------------------------------------------------------------------------------*/
jQuery(window).load(function(){
	//Top Slider
	$('.flexslider.top_slider').flexslider({
		animation: "fade",
		slideshowSpeed: 4000,
		controlNav: false,
		directionNav: true,
		pauseOnAction: false,
		pauseOnHover: true,
		prevText: "",
		nextText: ""
	});
	
	
	//Testimonials Carousel
	$('.flexslider.testimonials_carousel').flexslider({
		animation: "slide",
		slideshowSpeed: 4000,
		controlNav: true,
		directionNav: false,
		pauseOnAction: false,
		pauseOnHover: true,
		prevText: "",
		nextText: ""
	});
	
	
	$('.flexslider .flex-direction-nav a.flex-prev').prepend('<i class="fa fa-angle-left"></i>');
	$('.flexslider .flex-direction-nav a.flex-next').prepend('<i class="fa fa-angle-right"></i>');
	
});

jQuery(window).resize(function(){
	homeHeight();
	
});

jQuery(document).ready(function(){
	homeHeight();
});

function homeHeight(){
	var wh = jQuery(window).height();
	jQuery('.top_slider, .top_slider .slides li, .top_slider .slides li .container').css('height', wh);
	
	
}


/*-----------------------------------------------------------------------------------*/
/*	PARRALAX
/*-----------------------------------------------------------------------------------*/
jQuery(window).load(function() {
	if (jQuery(window).width() > 1025){
		jQuery('.flexslider.top_slider .slides li').parallax("0", -0.5);
		jQuery('#testimonials').parallax("0", -0.5);
		jQuery('#facts').parallax("0", -0.5);
		jQuery('footer').parallax("0", -0.5);
	}
	
});




/*-----------------------------------------------------------------------------------*/
/*	PRETTYPHOTO
/*-----------------------------------------------------------------------------------*/
jQuery(document).ready(function() {
	jQuery("a[rel^='prettyPhoto']").prettyPhoto();
});


/*-----------------------------------------------------------------------------------*/
/*	Контакты
/*-----------------------------------------------------------------------------------*/
jQuery(document).ready(function() {
	$("#ajax-contact-form").submit(function() {
		var str = $(this).serialize();		
		$.ajax({
			type: "POST",
			url: "contact_form/contact_process.php",
			data: str,
			success: function(msg) {
				// Ниже текст после отправки сообщения
				if(msg == 'OK') {
					result = '<div class="notification_ok">Ваше сообщение было отправлено. Спасибо!</div>';
					$("#fields").hide();
				} else {
					result = msg;
				}
				$('#note').html(result);
			}
		});
		return false;
	});
});



/*-----------------------------------------------------------------------------------*/
/*	IFRAME TRANSPARENT
/*-----------------------------------------------------------------------------------*/
jQuery(document).ready(function() {
	$("iframe").each(function(){
		var ifr_source = $(this).attr('src');
		var wmode = "wmode=transparent";
		if(ifr_source.indexOf('?') != -1) {
		var getQString = ifr_source.split('?');
		var oldString = getQString[1];
		var newString = getQString[0];
		$(this).attr('src',newString+'?'+wmode+'&'+oldString);
		}
		else $(this).attr('src',ifr_source+'?'+wmode);
	});
});




/*-----------------------------------------------------------------------------------*/
/*	SKILLS
/*-----------------------------------------------------------------------------------*/
jQuery(document).ready(function(){
	jQuery('.our-skills').waypoint(function() {
		setTimeout(function() {
			jQuery('.progress-complete').each(function(index) {
				var slidewidth = jQuery(this).attr('data-width');
				jQuery(this).animate({width:slidewidth}, 800);
			});
		}, 1000);
	}, {offset: '100%'});
});

